query feedQuery($locale: String!, $countryCode: String, $offset: Int, $postsPerPage: Int, $category: String) {
    TransientStream {
        myTransientFeed(countryCode: $countryCode, locale: $locale) {
            id
            activity {
                # TODO Comment in to enable welcome post when requirements are finalized
                # ...on SimpleActivity {
                #     type
                #     created_at
                # }
                ...on LinkAccountActivity {
                    type
                    created_at
                    platforms
                }
                ...on SuggestedFriendsActivity {
                    type
                    created_at
                    platform
                    suggestions {
                        epicId
                        epicDisplayName
                        platformFullName
                        platformAvatar
                    }
                }
                ...on IncomingInvitesActivity {
                    type
                    created_at
                    invites {
                        epicId
                        epicDisplayName
                    }
                }
                ...on RecentPlayersActivity {
                    type
                    created_at
                    players {
                        epicId
                        epicDisplayName
                        playedGameName
                    }
                }
            }
        }
    }
    Blog {
        dieselBlogPosts: getPosts(locale: $locale, offset: $offset, postsPerPage: $postsPerPage, category: $category) {
            blogList {
                _id
                author
                category
                content
                urlPattern
                slug
                sticky
                title
                date
                image
                shareImage
                trendingImage
                url
                featured
                link
                externalLink
            }
        }
    }
}
